import{_ as o,c as t,o as l,N as e}from"./chunks/framework.1f285265.js";const k=JSON.parse('{"title":"linux环境下安装nginx","description":"","frontmatter":{"title":"linux环境下安装nginx","date":"2018-01-15T22:00:42.000Z","tags":["node"]},"headers":[],"relativePath":"posts/前端/以前/linux环境下安装nginx.md"}'),i={name:"posts/前端/以前/linux环境下安装nginx.md"},n=e('<p><img src="https://aymfx.github.io/img/a201801/b1.jpg" alt="美女"></p><h1 id="前言" tabindex="-1">前言 <a class="header-anchor" href="#前言" aria-label="Permalink to &quot;前言&quot;">​</a></h1><blockquote><p>由于需要我自己有几个二级域名需要配置，还有多起几个node服务用nginx做方向代理，使得二级域名可以访问自己所在的目录</p></blockquote><h3 id="安装" tabindex="-1">安装 <a class="header-anchor" href="#安装" aria-label="Permalink to &quot;安装&quot;">​</a></h3><ol><li>安装gcc（centos 7之后一般已自带，可以在第6步失败后再安装）</li></ol><blockquote><p>yum install gcc gcc-c++</p></blockquote><ol start="2"><li>安装pcre</li></ol><blockquote><p>yum install -y pcre pcre-devel</p></blockquote><ol start="3"><li>安装zlib</li></ol><blockquote><p>yum install -y zlib zlib-devel</p></blockquote><ol start="4"><li>安装openssl</li></ol><blockquote><p>yum install -y openssl openssl-devel</p></blockquote><ol start="5"><li>下载并解压Nginx（之后进入Nginx目录</li></ol><blockquote><p>wget <a href="http://nginx.org/download/nginx-1.9.8.tar.gz" target="_blank" rel="noreferrer">http://nginx.org/download/nginx-1.9.8.tar.gz</a> //(可以装最新版本)</p></blockquote><blockquote><p>tar zxvf nginx-1.9.8.tar.gz</p></blockquote><blockquote><p>cd zxvf nginx-1.9.8</p></blockquote><ol start="6"><li>编译Nginx（加载常用模块如ssl）</li></ol><blockquote><p>./configure --prefix=/usr/local/nginx &gt;--with-http_stub_status_module --with-http_gzip_static_module &gt;--with-http_ssl_module</p></blockquote><ol start="7"><li>安装Nginx</li></ol><blockquote><p>make &amp;&amp; make install</p></blockquote><ol start="8"><li>启动</li></ol><blockquote><p>/usr/local/nginx/sbin/nginx</p></blockquote><ol start="9"><li>停止</li></ol><blockquote><p>/usr/local/nginx/sbin/nginx -s stop（reload表示重启）</p></blockquote><ol start="10"><li>浏览器访问Nginx所在机器IP，验证Nginx启动成功</li></ol><blockquote><p><a href="http://yourhost/" target="_blank" rel="noreferrer">http://yourhost/</a></p></blockquote><p><img src="https://aymfx.github.io/img/a201801/b3.png" alt="美女"></p><p>注：Nginx配置文件位置</p><blockquote><p>/usr/local/nginx/conf/nginx.conf</p></blockquote><p>##修改配置文件实现反向代理</p><p>1.进入配置文件</p><blockquote><p>vim /usr/local/nginx/conf/nginx.conf</p></blockquote><blockquote><p>找到下面的地方</p></blockquote><p><img src="https://aymfx.github.io/img/a201801/b4.png" alt="美女"></p><blockquote><p>标红的地方进行修改，其余注释</p></blockquote><p><img src="https://aymfx.github.io/img/a201801/b5.png" alt="美女"></p><blockquote><p>浏览器显示</p></blockquote><p><img src="https://aymfx.github.io/img/a201801/b6.png" alt="美女"></p>',38),a=[n];function p(c,s,r,u,g,b){return l(),t("div",null,a)}const _=o(i,[["render",p]]);export{k as __pageData,_ as default};
